import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";


function Slider_Data({ match }) {
  const [user, fetchUser] = useState([]);
  const params = useParams();
    // getdata funtion fetch data api
  const getData = async () => {
   await fetch(`https://jsonplaceholder.typicode.com/users/${params.id}`)
      .then(res => res.json())
      .then(json => fetchUser(json));
  };

  // call data effect
  useEffect(() => {
    getData();
  }, []);

  return (

  // <div className="col-5 box-center">
  //     <div className="card bg-white text-center text-white">
  //       <div className="card-body h-100">
  //         <h3 className="card-title text-nowrap text-dark">FullName :- {user.name}</h3>
  //         <h3 className="card-title text-nowrap mt-2 text-dark">Email :- {user.email}</h3>
  //       </div>
  //     </div>
  //   </div>

    <div class="card">
    <div class="cover-photo">
        <img src="https://i.imgur.com/KykRUCV.jpeg" class="profile"/>
    </div>
    <h3 class="profile-name">{user.name}</h3>
    <p class="about">{user.email} <br/> Front End Developer</p>
    {/* <button class="btn">Message</button>
    <button class="btn">Following</button> */}
    <div class="icons">
        <i class="fa-brands fa-linkedin"></i>
        <i class="fa-brands fa-github"></i>
        <i class="fa-brands fa-youtube"></i>
        <i class="fa-brands fa-twitter"></i>
    </div>
</div>


  );
}

export default Slider_Data;
